import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { useNavigate } from "react-router";
import { toast } from "react-toastify";
import Nav from "../../../../Nav";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";

const AddProject = ({ projects, addProject }) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");
  const [location, setLocation] = useState("");
  const [isEmpty, setIsEmpty] = useState(true);
  const [isError, setIsError] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    console.log(isError);
  }, [isError]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const checkProjectNameExists = projects.filter((project) =>
      project.name === name ? project : null
    );

    if (!type || !name || !desc || !teamNeeded || !level || !location) {
      return toast.warning("Please fill in all fields!!");
    }
    if (checkProjectNameExists.length > 0) {
      return toast.error("This project name already exists!!");
    }

    const data = {
      id: projects.length > 0 ? projects[projects.length - 1].id + 1 : 0,
      desc,
      name,
      level,
      teamNeeded,
      location,
      type,
    };

    addProject(data);
    toast.success("Your Request sent successfully!!");
    navigate("/myprojects");
  };

  return (
    <>
      <Nav />
      <Container
        component="main"
        maxWidth="xs"
        sx={{ paddingTop: "105px", paddingBottom: "100px" }}
      >
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          {/* <Avatar sx={{ m: 1, bgcolor: "#00468B" }}>
            <AddIcon />
          </Avatar> */}
          <Typography
            sx={{
              fontFamily: "Comfortaa",
              color: "#00468B",
              fontWeight: "800",
              fontSize: "40px",
              marginBottom: "30px",
            }}
            component="h1"
            variant="h5"
          >
            Add Project
          </Typography>
          <Box
            onSubmit={(e) => {
              if (isEmpty) {
                e.preventDefault();
                setIsEmpty(true);
                setIsError(true);
              } else {
                setIsEmpty(false);
              }
            }}
            sx={{
              width: 500,
              maxWidth: "100%",
            }}
          >
            <form onSubmit={handleSubmit}>
              <Grid container spacing={2}>

                <Grid item xs={12} sm={12}>
                  <TextField
                    fullWidth
                    label="Project name"
                    error={isError}
                    required
                    autoFocus
                    onChange={(e) => {
                      setName(e.target.value);
                      if (name.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <TextField
                    fullWidth
                    label="What is the project idea"
                    error={isError}
                    required
                    onChange={(e) => {
                      setDesc(e.target.value);
                      if (desc.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <Autocomplete
                    value={type}
                    onChange={(event, newValue) => {
                      setType(newValue);
                    }}
                    options={projectsType}
                    renderInput={(params) => (
                      <TextField {...params} label="Project type" />
                    )}
                  />
                </Grid>



                <Grid item xs={12} sm={12}>
                  <TextField
                    fullWidth
                    label="Where are You From"
                    id="fullWidth"
                    error={isError}
                    name="projectDesc"
                    required
                    onChange={(e) => {
                      setLocation(e.target.value);
                      if (location.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <TextField
                    fullWidth
                    label="Number of Team"
                    id="fullWidth"
                    error={isError}
                    name="teamNeeded"
                    required
                    onChange={(e) => {
                      setTeamNeeded(e.target.value);
                      if (teamNeeded.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <TextField
                    fullWidth
                    label="Level of Your Project"
                    id="fullWidth"
                    error={isError}
                    name="level"
                    required
                    onChange={(e) => {
                      setLevel(e.target.value);
                      if (level.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>
              </Grid>

              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{
                  ":hover": {
                    bgcolor: "#e4a300",
                  },
                  mt: 3,
                  mb: 2,
                  backgroundColor: "#FFB703",
                }}
              >
                Add Project
              </Button>
            </form>
          </Box>
        </Box>
      </Container>
    </>
  );
};

const mapStateToProps = (state) => ({
  projects: state,
});
const mapDispatchToProps = (dispatch) => ({
  addProject: (data) => {
    dispatch({ type: "ADD_PROJECT", payload: data });
  },
});

const projectsType = [
  "Web Application",
  "Android Application",
  "IOS Application",
  "Security System",
  "UI",
  "UX",
];

const projectsLevel = ["Guru", "Advanced", "Intermediate", "Junior", "Newbie"];

export default connect(mapStateToProps, mapDispatchToProps)(AddProject);
