import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function AddProjects({ setProject }) {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");
  const navigate = useNavigate();

  function handleSubmit(e) {
    e.preventDefault();
    axios
      .post("/projects", {
        name,
        type,
        desc,
        teamNeeded,
        level,
      })
      .then((res) => {
        console.log(res.data);
        // setProject((name, type, desc, teamNeeded, level));

        navigate("/projects", {
          state: {
            name: res.data.name,
            type: res.data.type,
            desc: res.data.type,
            teamNeeded: res.data.type,
            level: res.data.type,
          },
        });
      })
      .catch((err) => {
        console.log(err.res);
      });
  }

  return (
    <>
      <h1>Enter your project details</h1>
      <Form id="formName" onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Enter your project name:</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter name"
            id="name"
            name="name"
            onChange={(e) => {
              setName(e.target.value);
            }}
          />

          <Form.Label>Enter your project type:</Form.Label>
          <Form.Control
            type="type"
            placeholder="Enter type"
            id="type"
            name="type"
            onChange={(e) => {
              setType(e.target.value);
            }}
          />

          <Form.Label>Enter your project description:</Form.Label>
          <Form.Control
            type="description"
            placeholder="Enter description"
            id="description"
            name="description"
            onChange={(e) => {
              setDesc(e.target.value);
            }}
          />

          <Form.Label>How many members you need:</Form.Label>
          <Form.Control
            type="member"
            placeholder="Enter member"
            id="member"
            name="member"
            onChange={(e) => {
              setTeamNeeded(e.target.value);
            }}
          />

          <Form.Label>Enter the level of your project:</Form.Label>
          <Form.Control
            type="level"
            placeholder="Enter level"
            id="level"
            name="level"
            onChange={(e) => {
              setLevel(e.target.value);
            }}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </>
  );
}
