import * as React from "react";
import { styled } from "@mui/material/styles";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import Collapse from "@mui/material/Collapse";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import { red } from "@mui/material/colors";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ShareIcon from "@mui/icons-material/Share";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import GroupsIcon from "@mui/icons-material/Groups";
import GradeIcon from "@mui/icons-material/Grade";
import Button from "@mui/material/Button";
import Alert from "@mui/material/Alert";
import { toast } from "react-toastify";


export default function DisplayProjects(props) {
  const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    large: {
      fontSize: "2.5rem",
      width: 100,
      height: 100
    }
  })
);
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardHeader
        avatar={
          <Avatar src={props.url} className={classes[Math.floor(Math.random() * 3)]} aria-label="recipe">

          </Avatar>
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        title={props.leader}
        subheader={props.date}
      />
      <hr></hr>
      <Typography sx={{ marginLeft: "15px" }} variant="h4" component="div">
        {props.name}
      </Typography>

      <CardContent>
        <Typography variant="body2" color="text.secondary">
          {props.desc}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <LocationOnIcon
          sx={{ fontSize: "18px", marginRight: "5px" }}
        ></LocationOnIcon>{" "}
        <Typography sx={{ paddingRight: "15px" }}>{props.location}</Typography>
        <GroupsIcon
          sx={{ fontSize: "23px", marginRight: "7px" }}
        ></GroupsIcon>{" "}
        <Typography sx={{ marginRight: "15px" }}>{props.team}</Typography>
        <GradeIcon
          sx={{ fontSize: "18px", marginRight: "5px" }}
        ></GradeIcon>{" "}
        <Typography sx={{ marginRight: "20px" }}>{props.level}</Typography>
      </CardActions>
      <Button
        type="submit"

        variant="contained"
        sx={{
          ":hover": {
            bgcolor: "#e4a300",
          },
          mt: 3,
          mb: 2,
          backgroundColor: "#FFB703",
          marginLeft:"10px",
          marginRight:"10px",
          marginBottom:"20px",
          width:"94%"

        }}
        onClick={()=>toast.success("Your request has been sent!!")}
      >
        Apply
      </Button>
    </Card>
  );
}
