import React, { useState } from "react";
import { connect } from "react-redux";
import { useNavigate } from "react-router";
import { toast } from "react-toastify";
import Nav from "../../../../Nav";
import axios from "axios";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Link from "@mui/material/Link";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Toolbar from "@mui/material/Toolbar";
const AddProject = ({ projects, addProject }) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");
  const [location, setLocation] = useState("");

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const checkProjectNameExists = projects.filter((project) =>
      project.name === name ? project : null
    );

    if (!type || !name || !desc || !teamNeeded || !level || !location) {
      return toast.warning("Please fill in all fields!!");
    }
    if (checkProjectNameExists.length > 0) {
      return toast.error("This project name already exists!!");
    }

    const data = {
      id: projects.length > 0 ? projects[projects.length - 1].id + 1 : 0,
      desc,
      name,
      level,
      teamNeeded,
      location,
      type,
    };

    addProject(data);
    toast.success("Project added successfully!!");
    navigate("/myprojects");
  };

  return (
    <>
      <Nav />

      <ThemeProvider>
        <Toolbar id="back-to-top-anchor" />
        <Container
          component="main"
          maxWidth="xs"
          sx={{ paddingTop: "90px", paddingBottom: "170px" }}
        >
          <CssBaseline />
          <Box
            sx={{
              marginTop: 8,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: "#00468B" }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography
              sx={{
                fontFamily: "Comfortaa",
                color: "#00468B",
                fontWeight: "800",
                marginTop: "20px",
                fontSize: "40px",
              }}
              component="h1"
              variant="h5"
            >
              Add Project
            </Typography>
            <Box
              component="form"
              noValidate
              onSubmit={handleSubmit}
              sx={{ mt: 3 }}
            >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    autoComplete="given-name"
                    name="projectName"
                    required
                    fullWidth
                    id="projectName"
                    label="Project Name"
                    autoFocus
                    onChange={(e) => setName(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="desc"
                    label="Project Description"
                    name="desc"
                    autoComplete="family-name"
                    onChange={(e) => setDesc(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="type"
                    label="Project type"
                    name="type"
                    autoComplete="type"
                    onChange={(e) => setType(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    name="team"
                    label="Team Needed"
                    type="text"
                    id="team"
                    autoComplete="new-password"
                    onChange={(e) => setTeamNeeded(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    name="level"
                    label="Project Level"
                    type="text"
                    id="level"
                    autoComplete="new-password"
                    onChange={(e) => setLevel(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    name="location"
                    label="Your Location"
                    type="text"
                    id="location"
                    autoComplete="new-password"
                    onChange={(e) => setLocation(e.target.value)}
                  />
                </Grid>
              </Grid>

              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{
                  ":hover": {
                    bgcolor: "#e4a300",
                  },
                  mt: 3,
                  mb: 2,
                  backgroundColor: "#FFB703",
                }}
                onClick={() => {}}
              >
                Add Project
              </Button>
            </Box>
          </Box>
        </Container>
      </ThemeProvider>

