import React, { useState } from "react";
import { connect } from "react-redux";
import { useNavigate } from "react-router";
import { toast } from "react-toastify";
import Nav from "../../../../Nav";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Container from "@mui/material/Container";

const AddProject = ({ projects, addProject }) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");
  const [location, setLocation] = useState("");
  const [isEmpty, setIsEmpty] = useState(true);
  const [isError, setIsError] = useState(false);

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const checkProjectNameExists = projects.filter((project) =>
      project.name === name ? project : null
    );

    if (!type || !name || !desc || !teamNeeded || !level || !location) {
      return toast.warning("Please fill in all fields!!");
    }
    if (checkProjectNameExists.length > 0) {
      return toast.error("This project name already exists!!");
    }

    const data = {
      id: projects.length > 0 ? projects[projects.length - 1].id + 1 : 0,
      desc,
      name,
      level,
      teamNeeded,
      location,
      type,
    };

    addProject(data);
    toast.success("Your Request sent successfully!!");
    navigate("/myprojects");
  };

  return (
    <>
      <Nav />
      <Container
        component="main"
        maxWidth="xs"
        sx={{ paddingTop: "90px", paddingBottom: "170px" }}
      >
        <Box
          sx={{
            width: 500,
            maxWidth: "100%",
          }}
        >
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Project Name"
            id="fullWidth"
            error={isError}
            autoComplete="given-name"
            name="projectName"
            required
            autoFocus
            onChange={(e) => {
              setName(e.target.value);
              if (name.length !== 0) {
                setIsEmpty(false);
              }
            }}
          />
          </Grid>
        </Box>
      </Container>

      <div style={{ paddingTop: "180px", paddingBottom: "120px" }}>
        <h1
          className="text-center py-3 display-2"
          style={{ fontSize: "40px", fontWeight: "600", color: "#00468B" }}
        >
          Add Project
        </h1>
        <div style={{ color: "white" }}>
          <div className="col-md-6 p-5 mx-auto shadow">
            <form onSubmit={handleSubmit}>
              <div className="form-group" style={{ marginBottom: "10px" }}>
                <input
                  className="form-control"
                  value={name}
                  placeholder={"Project Name"}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
              <div className="form-group" style={{ marginBottom: "10px" }}>
                <input
                  className="form-control"
                  value={desc}
                  placeholder={"Description"}
                  onChange={(e) => setDesc(e.target.value)}
                />
              </div>
              <div className="form-group" style={{ marginBottom: "10px" }}>
                <input
                  className="form-control"
                  value={type}
                  placeholder={"Project Type"}
                  onChange={(e) => setType(e.target.value)}
                />
              </div>
              <div className="form-group" style={{ marginBottom: "10px" }}>
                <input
                  className="form-control"
                  value={teamNeeded}
                  placeholder={"Teem needed"}
                  onChange={(e) => setTeamNeeded(e.target.value)}
                />
              </div>
              <div className="form-group" style={{ marginBottom: "10px" }}>
                <input
                  className="form-control"
                  value={location}
                  placeholder={"Where are you"}
                  onChange={(e) => setLocation(e.target.value)}
                />
              </div>
              <div className="form-group" style={{ marginBottom: "10px" }}>
                <input
                  className="form-control"
                  value={level}
                  placeholder={"Whats the level of project?"}
                  onChange={(e) => setLevel(e.target.value)}
                />
              </div>
              <div className="form-group">
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  sx={{
                    ":hover": {
                      bgcolor: "#e4a300",
                    },
                    mt: 3,
                    mb: 2,
                    backgroundColor: "#FFB703",
                  }}
                >
                  Add Project
                </Button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

const mapStateToProps = (state) => ({
  projects: state,
});
const mapDispatchToProps = (dispatch) => ({
  addProject: (data) => {
    dispatch({ type: "ADD_PROJECT", payload: data });
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(AddProject);
