import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { useNavigate, useParams } from "react-router";
import { toast } from "react-toastify";
import Button from "@mui/material/Button";


import Nav from "../../../../Nav";
const UpdateProject = ({ projects, updateProject }) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");
  const [location, setLocation] = useState("");

  const { id } = useParams();
  const navigate = useNavigate();

  const currentProject = projects.find(
    (project) => project.id === parseInt(id)
  );

  useEffect(() => {
    setName(currentProject.name);
    setType(currentProject.type);
    setDesc(currentProject.desc);
    setTeamNeeded(currentProject.teamNeeded);
    setLevel(currentProject.level);
    setLocation(currentProject.location);
  }, [currentProject]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const checkProjectNameExists = projects.filter((project) =>
      project.name === name && project.id !== currentProject.id ? project : null
    );

    if (!type || !name || !desc || !teamNeeded || !level || !location) {
      return toast.warning("Please fill in all fields!!");
    }
    if (checkProjectNameExists.length > 0) {
      return toast.error("This project name already exists!!");
    }

    const data = {
      id: currentProject.id,
      desc,
      name,
      level,
      teamNeeded,
      location,
      type,
    };

    updateProject(data);
    toast.success("Project updated successfully!!");
    navigate("/myprojects");
  };

  return (
    <>
      <Nav />

      <Container
        component="main"
        maxWidth="xs"
        sx={{ paddingTop: "105px", paddingBottom: "100px" }}
      >
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          {/* <Avatar sx={{ m: 1, bgcolor: "#00468B" }}>
            <AddIcon />
          </Avatar> */}
          <Typography
            sx={{
              fontFamily: "Comfortaa",
              color: "#00468B",
              fontWeight: "800",
              fontSize: "40px",
              marginBottom: "30px",
            }}
            component="h1"
            variant="h5"
          >
            Add Project
          </Typography>
          <Box
            onSubmit={(e) => {
              if (isEmpty) {
                e.preventDefault();
                setIsEmpty(true);
                setIsError(true);
              } else {
                setIsEmpty(false);
              }
            }}
            sx={{
              width: 500,
              maxWidth: "100%",
            }}
          >
            <form onSubmit={handleSubmit}>


            </form>
          </Box>
        </Box>
      </Container>

      <div style={{ paddingTop: "150px", paddingBottom: "150px" }}>
        <div className="row d-flex flex-column">
          <h1
            className="text-center py-3 display-2"
            style={{ fontSize: "40px", fontWeight: "600", color: "#00468B" }}
          >
            Update Your Project
          </h1>
          <div className="col-md-6 mx-auto shadow p-5">
            {currentProject ? (
              <form onSubmit={handleSubmit}>
                <div className="form-group" style={{ marginBottom: "10px" }}>
                  <input
                    className="form-control"
                    value={name}
                    placeholder={"Project Name"}
                    onChange={(e) => setName(e.target.value)}
                  />
                </div>
                <div className="form-group" style={{ marginBottom: "10px" }}>
                  <input
                    className="form-control"
                    value={desc}
                    placeholder={"Description"}
                    onChange={(e) => setDesc(e.target.value)}
                  />
                </div>
                <div className="form-group" style={{ marginBottom: "10px" }}>
                  <input
                    className="form-control"
                    value={type}
                    placeholder={"Project Type"}
                    onChange={(e) => setType(e.target.value)}
                  />
                </div>
                <div className="form-group" style={{ marginBottom: "10px" }}>
                  <input
                    className="form-control"
                    value={teamNeeded}
                    placeholder={"Teem needed"}
                    onChange={(e) => setTeamNeeded(e.target.value)}
                  />
                </div>
                <div className="form-group" style={{ marginBottom: "10px" }}>
                  <input
                    className="form-control"
                    value={location}
                    placeholder={"Where are you"}
                    onChange={(e) => setLocation(e.target.value)}
                  />
                </div>
                <div className="form-group" style={{ marginBottom: "10px" }}>
                  <input
                    className="form-control"
                    value={level}
                    placeholder={"Whats the level of project?"}
                    onChange={(e) => setLevel(e.target.value)}
                  />
                </div>
                <div className="form-group d-flex align-items-center justify-content-between my-2">
                  <Button
                    type="submit"
                    variant="contained"
                    sx={{
                      ":hover": {
                        bgcolor: "#e4a300",
                      },
                      maxWidth: "180px",
                      maxHeight: "30px",
                      minWidth: "180px",
                      minHeight: "30px",

                      mt: 3,
                      mb: 2,
                      backgroundColor: "#FFB703",
                    }}
                  >
                    Update
                  </Button>

                  <Button
                    type="submit"
                    variant="contained"
                    sx={{
                      ":hover": {
                        bgcolor: "#e4a300",
                      },
                      maxWidth: "180px",
                      maxHeight: "30px",
                      minWidth: "180px",
                      minHeight: "30px",
                      mt: 3,
                      mb: 2,
                      backgroundColor: "#FFB703",
                    }}
                    onClick={() => navigate("/myprojects")}
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            ) : (
              <h1 className="text-center">No Project Found</h1>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

const mapStateToProps = (state) => ({
  projects: state,
});
const mapDispatchToProps = (dispatch) => ({
  updateProject: (data) => {
    dispatch({ type: "UPDATE_PROJECT", payload: data });
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(UpdateProject);
