            <Box
              component="form"
              noValidate
              onSubmit={(e) => {
                if (isEmpty) {
                  e.preventDefault();
                  setIsEmpty(true);
                  setIsError(true);
                } else {
                  setIsEmpty(false);
                  handleSubmit(e);
                }
              }}
              sx={{ mt: 3 }}
            >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    error={isError}
                    autoComplete="given-name"
                    name="firstName"
                    required
                    fullWidth
                    id="firstName"
                    label="First Name"
                    autoFocus
                    onChange={(e) => {
                      setFirstName(e.target.value);
                      if (firstName.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    error={isError}
                    required
                    fullWidth
                    id="lastName"
                    label="Last Name"
                    name="lastName"
                    autoComplete="family-name"
                    onChange={(e) => {
                      setLastName(e.target.value);
                      if (lastName.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    error={isError}
                    required
                    fullWidth
                    id="email"
                    label="Email Address"
                    name="email"
                    autoComplete="email"
                    onChange={(e) => {
                      setEmail(e.target.value);
                      if (email.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    error={isError}
                    required
                    fullWidth
                    name="password"
                    label="Password"
                    type="password"
                    id="password"
                    autoComplete="new-password"
                    onChange={(e) => {
                      setPassword(e.target.value);
                      if (password.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>
              </Grid>
