            }}
          >
            <Typography
              sx={{
                fontFamily: "Comfortaa",
                color: "#00468B",
                fontWeight: "800",
                marginTop: "20px",
                fontSize: "40px",
              }}
              component="h1"
              variant="h5"
            >
              Add Project
            </Typography>
            <Box
              component="form"
              noValidate
              onSubmit={handleSubmit}
              sx={{ mt: 3 }}
            >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    autoComplete="given-name"
                    name="projectName"
                    required
                    fullWidth
                    id="projectName"
                    label="Project Name"
                    autoFocus
                    onChange={(e) => setName(e.target.value)}
                  />
                <Grid/>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="desc"
                    label="Project Description"
                    name="desc"
                    autoComplete="family-name"
                    onChange={(e) => setDesc(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="type"
                    label="Project type"
                    name="type"
                    autoComplete="type"
                    onChange={(e) => setType(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    name="team"
                    label="Team Needed"
                    type="text"
                    id="team"
                    autoComplete="new-password"
                    onChange={(e) => setTeamNeeded(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    name="level"
                    label="Project Level"
                    type="text"
                    id="level"
                    autoComplete="new-password"
                    onChange={(e) => setLevel(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    name="location"
                    label="Your Location"
                    type="text"
                    id="location"
                    autoComplete="new-password"
                    onChange={(e) => setLocation(e.target.value)}
                  />
                </Grid>
              </Grid>

              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{
                  ":hover": {
                    bgcolor: "#e4a300",
                  },
                  mt: 3,
