      <div style={{paddingTop:"100px"}}>
        <h1 className="text-center py-3 display-2" style={{fontSize:"40px", fontWeight:"600", color:"#00468B"}}>Add Project</h1>
        <div style={{color:"white"}}>
          <div className="col-md-6 p-5 mx-auto shadow">
            <form onSubmit={handleSubmit}>
              <div className="form-group" style={{marginBottom:"10px"}}>
                <input
                  className="form-control"
                  value={name}
                  placeholder={"Project Name"}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
              <div className="form-group" style={{marginBottom:"10px"}}>
                <input
                  className="form-control"
                  value={desc}
                  placeholder={"Description"}
                  onChange={(e) => setDesc(e.target.value)}
                />
              </div>
              <div className="form-group" style={{marginBottom:"10px"}}>
                <input
                  className="form-control"
                  value={type}
                  placeholder={"Project Type"}
                  onChange={(e) => setType(e.target.value)}
                />
              </div>
              <div className="form-group" style={{marginBottom:"10px"}}>
                <input
                  className="form-control"
                  value={teamNeeded}
                  placeholder={"Teem needed"}
                  onChange={(e) => setTeamNeeded(e.target.value)}
                />
              </div>
              <div className="form-group">
                <input
                  className="form-control"
                  value={location}
                  placeholder={"Where are you"}
                  onChange={(e) => setLocation(e.target.value)}
                />
              </div>
              <div className="form-group">
                <input
                  className="form-control"
                  value={level}
                  placeholder={"Whats the level of project?"}
                  onChange={(e) => setLevel(e.target.value)}
                />

              </div>
              <div className="form-group">
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{
                  ":hover": {
                    bgcolor: "#e4a300",
                  },
                  mt: 3,
                  mb: 2,
                  backgroundColor: "#FFB703",
                }}

              >
                Add Project
              </Button>

              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

const mapStateToProps = (state) => ({
  projects: state,
});
const mapDispatchToProps = (dispatch) => ({
  addProject: (data) => {
    dispatch({ type: "ADD_PROJECT", payload: data });
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(AddProject);
