import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { useNavigate, useParams } from "react-router";
import { toast } from "react-toastify";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";


import Nav from "../../../../Nav";
const UpdateProject = ({ projects, updateProject }) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");
  const [location, setLocation] = useState("");
  const [isEmpty, setIsEmpty] = useState(true);
  const [isError, setIsError] = useState(false);

  const { id } = useParams();
  const navigate = useNavigate();

  const currentProject = projects.find(
    (project) => project.id === parseInt(id)
  );

  useEffect(() => {
    setName(currentProject.name);
    setType(currentProject.type);
    setDesc(currentProject.desc);
    setTeamNeeded(currentProject.teamNeeded);
    setLevel(currentProject.level);
    setLocation(currentProject.location);
  }, [currentProject]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const checkProjectNameExists = projects.filter((project) =>
      project.name === name && project.id !== currentProject.id ? project : null
    );

    if (!type || !name || !desc || !teamNeeded || !level || !location) {
      return toast.warning("Please fill in all fields!!");
    }
    if (checkProjectNameExists.length > 0) {
      return toast.error("This project name already exists!!");
    }

    const data = {
      id: currentProject.id,
      desc,
      name,
      level,
      teamNeeded,
      location,
      type,
    };

    updateProject(data);
    toast.success("Project updated successfully!!");
    navigate("/myprojects");
  };

  return (
    <>
      <Nav />

      <Container
        component="main"
        maxWidth="xs"
        sx={{ paddingTop: "99px", paddingBottom: "99px" }}
      >
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Typography
            sx={{
              fontFamily: "Comfortaa",
              color: "#00468B",
              fontWeight: "800",
              fontSize: "40px",
              marginBottom: "30px",
            }}
            component="h1"
            variant="h5"
          >
            Update Project
          </Typography>
          <Box
            onSubmit={(e) => {
              if (isEmpty) {
                e.preventDefault();
                setIsEmpty(true);
                setIsError(true);
              } else {
                setIsEmpty(false);
              }
            }}
            sx={{
              width: 500,
              maxWidth: "100%",
            }}
          >
            {currentProject ? (
              <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={12}>
                    <TextField
                      value={name}
                      fullWidth
                      label="Project name"
                      error={isError}
                      required
                      autoFocus
                      onChange={(e) => {
                        setName(e.target.value);
                        if (name.length !== 0) {
                          setIsEmpty(false);
                        }
                      }}
                    />
                  </Grid>

                  <Grid item xs={12} sm={12}>
                    <TextField
                      value={desc}
                      fullWidth
                      label="Project idea"
                      error={isError}
                      required
                      onChange={(e) => {
                        setDesc(e.target.value);
                        if (desc.length !== 0) {
                          setIsEmpty(false);
                        }
                      }}
                    />
                  </Grid>

                  <Grid item xs={12} sm={12}>
                    <TextField
                      value={type}
                      fullWidth
                      label="Project type"
                      error={isError}
                      required
                      onChange={(e) => {
                        setType(e.target.value);
                        if (type.length !== 0) {
                          setIsEmpty(false);
                        }
                      }}
                    />
                  </Grid>

                  <Grid item xs={12} sm={12}>

                  <Autocomplete
                    value={location}
                    onChange={(event, newValue) => {
                      setLocation(newValue);
                    }}
                    options={countries}
                    autoHighlight
                    renderOption={(props, option) => (
                      <Box
                        component="li"
                        sx={{ "& > img": { mr: 2, flexShrink: 0 } }}
                        {...props}
                      >
                        <img
                          loading="lazy"
                          width="20"
                          src={`https://flagcdn.com/w20/${option.code.toLowerCase()}.png`}
                          srcSet={`https://flagcdn.com/w40/${option.code.toLowerCase()}.png 2x`}
                          alt=""
                        />
                        {option.label}
                      </Box>
                    )}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        label="Where are from"
                        inputProps={{
                          ...params.inputProps,
                          autoComplete: "new-password", 
                        }}
                      />
                    )}
                  />

                  {/* <Autocomplete
                    value={type}
                    onChange={(event, newValue) => {
                      setType(newValue);
                    }}
                    options={projectsType}
                    renderInput={(params) => (
                      <TextField {...params} label="Project type" />
                    )}
                  /> */}
                    {/* <TextField
                      value={location}
                      fullWidth
                      label="Where are from"
                      error={isError}
                      required
                      onChange={(e) => {
                        setLocation(e.target.value);
                        if (location.length !== 0) {
                          setIsEmpty(false);
                        }
                      }}
                    /> */}
                  </Grid>

                  <Grid item xs={12} sm={12}>
                    <TextField
                      value={teamNeeded}
                      fullWidth
                      label="Number of teammates"
                      error={isError}
                      required
                      onChange={(e) => {
                        setTeamNeeded(e.target.value);
                        if (teamNeeded.length !== 0) {
                          setIsEmpty(false);
                        }
                      }}
                    />
                  </Grid>

                  <Grid item xs={12} sm={12}>
                    <TextField
                      value={level}
                      fullWidth
                      label="Level of your project"
                      error={isError}
                      required
                      onChange={(e) => {
                        setLevel(e.target.value);
                        if (level.length !== 0) {
                          setIsEmpty(false);
                        }
                      }}
                    />
                  </Grid>
                </Grid>
                <div className="form-group d-flex align-items-center justify-content-between my-2">
                  <Button
                    type="submit"
                    variant="contained"
                    sx={{
                      ":hover": {
                        bgcolor: "#e4a300",
                      },
