import React, { useState } from "react";
import { connect } from "react-redux";
import { useNavigate } from "react-router";
import { toast } from "react-toastify";

const AddProject = ({ projects, addProject }) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");
  const [location, setLocation] = useState("");

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const checkProjectNameExists = projects.filter((project) =>
      project.name === name ? project : null
    );

    if (!type || !name || !desc || !teamNeeded || !level || !location) {
      return toast.warning("Please fill in all fields!!");
    }
    if (checkProjectNameExists.length > 0) {
      return toast.error("This project name already exists!!");
    }

    const data = {
      id: projects.length > 0 ? projects[projects.length - 1].id + 1 : 0,
      desc,
      name,
      level,
      teamNeeded,
      location,
      type,
    };

    addProject(data);
    toast.success("Project added successfully!!");
    navigate.push("/user/add");
  };

  return (
    <div className="container-fluid">
      <h1 className="text-center text-dark py-3 display-2">Add Post</h1>
      <div className="row">
        <div className="col-md-6 p-5 mx-auto shadow">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                className="form-control"
                value={name}
                placeholder={"Project Name"}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                className="form-control"
                value={desc}
                placeholder={"Description"}
                onChange={(e) => setDesc(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                className="form-control"
                value={type}
                placeholder={"Project Type"}
                onChange={(e) => setType(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                className="form-control"
                value={teamNeeded}
                placeholder={"Teem needed"}
                onChange={(e) => setTeamNeeded(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                className="form-control"
                value={location}
                placeholder={"Where are you"}
                onChange={(e) => setLocation(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                className="form-control"
                value={level}
                placeholder={"Whats the level of project?"}
                onChange={(e) => setLevel(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                className="btn btn-block btn-dark"
                type="submit"
                value="Add Student"
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  projects: state,
});
const mapDispatchToProps = (dispatch) => ({
  addProject: (data) => {
    dispatch({ type: "ADD_PROJECT", payload: data });
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(AddProject);
