import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

export default function Projects() {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");

  function handleSubmit(e) {
    // e.preventDefault();
    fetch("/project", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name, type, desc, teamNeeded, level }),
    })
      .then((res) => res.json())
      .then((response) => alert("Success:", JSON.stringify(response)))
      .catch((error) => console.log("Error:", error));
  }

  return (
    <>
      <h1>Enter your project details</h1>
      <Form id="formName" onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Enter your project name:</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter name"
            id="name"
            name="name"
            onChange={(e) => {
              setName(e.target.value);
            }}
          />

          <Form.Label>Enter your project type:</Form.Label>
          <Form.Control
            type="type"
            placeholder="Enter type"
            id="type"
            name="type"
            onChange={(e) => {
              setType(e.target.value);
            }}
          />

          <Form.Label>Enter your project description:</Form.Label>
          <Form.Control
            type="description"
            placeholder="Enter description"
            id="description"
            name="description"
            onChange={(e) => {
              setDesc(e.target.value);
            }}
          />

          <Form.Label>How many members you need:</Form.Label>
          <Form.Control
            type="member"
            placeholder="Enter member"
            id="password"
            name="password"
            onChange={(e) => {
              setTeamNeeded(e.target.value);
            }}
          />

          <Form.Label>Enter the level of ypur project:</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter password"
            id="password"
            name="password"
            onChange={(e) => {
              setLevel(e.target.value);
            }}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </>
  );
}
