import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { useNavigate } from "react-router";
import { toast } from "react-toastify";
import Nav from "../../../../Nav";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";

const AddProject = ({ projects, addProject }) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [teamNeeded, setTeamNeeded] = useState("");
  const [level, setLevel] = useState("");
  const [location, setLocation] = useState("");
  const [isEmpty, setIsEmpty] = useState(true);
  const [isError, setIsError] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    console.log(isError);
  }, [isError]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const checkProjectNameExists = projects.filter((project) =>
      project.name === name ? project : null
    );

    if (!type || !name || !desc || !teamNeeded || !level || !location) {
      return toast.warning("Please fill in all fields!!");
    }
    if (checkProjectNameExists.length > 0) {
      return toast.error("This project name already exists!!");
    }

    const data = {
      id: projects.length > 0 ? projects[projects.length - 1].id + 1 : 0,
      desc,
      name,
      level,
      teamNeeded,
      location,
      type,
    };

    addProject(data);
    toast.success("Your Request sent successfully!!");
    navigate("/myprojects");
  };

  return (
    <>
      <Nav />
      <Container
        component="main"
        maxWidth="xs"
        sx={{ paddingTop: "105px", paddingBottom: "100px" }}
      >
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          {/* <Avatar sx={{ m: 1, bgcolor: "#00468B" }}>
            <AddIcon />
          </Avatar> */}
          <Typography
            sx={{
              fontFamily: "Comfortaa",
              color: "#00468B",
              fontWeight: "800",
              fontSize: "40px",
              marginBottom: "30px",
            }}
            component="h1"
            variant="h5"
          >
            Add Project
          </Typography>
          <Box
            onSubmit={(e) => {
              if (isEmpty) {
                e.preventDefault();
                setIsEmpty(true);
                setIsError(true);
              } else {
                setIsEmpty(false);
              }
            }}
            sx={{
              width: 500,
              maxWidth: "100%",
            }}
          >
            <form onSubmit={handleSubmit}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={12}>
                  <TextField
                    fullWidth
                    label="Project name"
                    error={isError}
                    required
                    autoFocus
                    onChange={(e) => {
                      setName(e.target.value);
                      if (name.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <TextField
                    fullWidth
                    label="Project idea"
                    error={isError}
                    required
                    onChange={(e) => {
                      setDesc(e.target.value);
                      if (desc.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <Autocomplete
                    value={type}
                    onChange={(event, newValue) => {
                      setType(newValue);
                    }}
                    options={projectsType}
                    renderInput={(params) => (
                      <TextField {...params} label="Project type" />
                    )}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  {/* <Autocomplete
                    value={location || 'null'}
                    onChange={(event, newValue) => {
                      setLocation(newValue);
                    }}
                    options={countries}
                    autoHighlight
                    getOptionLabel={(option) =>
                      option.label ? option.label : ""
                    }
                    renderOption={(props, option) => (
                      <Box
                        component="li"
                        sx={{ "& > img": { mr: 2, flexShrink: 0 } }}
                        {...props}
                      >
                        <img
                          loading="lazy"
                          width="20"
                          src={`https://flagcdn.com/w20/${option.code.toLowerCase()}.png`}
                          srcSet={`https://flagcdn.com/w40/${option.code.toLowerCase()}.png 2x`}
                          alt=""
                        />
                        {option.label} ({option.code})
                      </Box>
                    )}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        label="Where are from"
                        inputProps={{
                          ...params.inputProps,
                          autoComplete: "new-password", // disable autocomplete and autofill
                        }}
                      />
                    )}
                  /> */}
                  <Autocomplete
                    value={location}
                    onChange={(event, newValue) => {
                      setLocation(newValue);
                    }}
                    options={countries}
                    renderInput={(params) => (
                      <TextField {...params} label="Where are from" />
                    )}
                  />
                </Grid>
                <Grid item xs={12} sm={12}>
                  <Autocomplete
                    disablePortal
                    value={teamNeeded}
                    onChange={(event, newValue) => {
                      setTeamNeeded(newValue);
                    }}
                    options={projectsTeam}
                    renderInput={(params) => (
                      <TextField {...params} label="Number of teammates" />
                    )}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <Autocomplete
                    value={level}
                    onChange={(event, newValue) => {
                      setLevel(newValue);
                    }}
                    options={projectsLevel}
                    renderInput={(params) => (
                      <TextField {...params} label="Level of your project" />
                    )}
                  />
                </Grid>
              </Grid>

              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{
                  ":hover": {
                    bgcolor: "#e4a300",
                  },
                  mt: 3,
                  mb: 2,
                  backgroundColor: "#FFB703",
                }}
              >
                Add Project
              </Button>
            </form>
          </Box>
        </Box>
      </Container>
    </>
  );
};

