import React, { useState } from "react";
import { connect } from "react-redux";
import { useNavigate } from "react-router";
import { toast } from "react-toastify";

import Autocomplete from "@mui/material/Autocomplete";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";
import Container from "@mui/material/Container";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";

const AddProject = ({ projects, addProject }, props) => {
  const [teamNeeded, setTeamNeeded] = useState("");
  const [isError, setIsError] = useState(false);
  const [location, setLocation] = useState("");
  const [isEmpty, setIsEmpty] = useState(true);
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [desc, setDesc] = useState("");
  const [level, setLevel] = useState("");


  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const checkProjectNameExists = projects.filter((project) =>
      project.name === name ? project : null
    );

    if (!type || !name || !desc || !teamNeeded || !level || !location) {
      return toast.warning("Please fill in all fields!!");
    }
    if (checkProjectNameExists.length > 0) {
      return toast.error("This project name already exists!!");
    }

    const data = {
      id: projects.length > 0 ? projects[projects.length - 1].id + 1 : 0,
      desc,
      name,
      level,
      teamNeeded,
      location,
      type,
    };

    addProject(data);
    toast.success("Your Request sent successfully!!");
    navigate("/myprojects");
  };

  return (
    <>
      <Container
        component="main"
        maxWidth="xs"
        sx={{ paddingTop: "40px", paddingBottom: "100px" }}
      >
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Typography
            sx={{
              fontFamily: "Comfortaa",
              // color: props.dark ? "#00468B" : "white",
              color: "#00468B",
              fontWeight: "800",
              fontSize: "40px",
              marginBottom: "30px",
            }}
            component="h1"
            variant="h5"
          >
            Add Project
          </Typography>
          <Box
            onSubmit={(e) => {
              if (isEmpty) {
                e.preventDefault();
                setIsEmpty(true);
                setIsError(true);
              } else {
                setIsEmpty(false);
              }
            }}
            sx={{
              width: 500,
              maxWidth: "100%",
            }}
          >
            <form onSubmit={handleSubmit}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={12}>
                  <TextField
                    variant="filled"
                    fullWidth
                    label="Project name"
                    error={isError}
                    required
                    autoFocus
                    onChange={(e) => {
                      setName(e.target.value);
                      if (name.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <TextField
                    variant="filled"
                    fullWidth
                    label="Project idea"
                    error={isError}
                    required
                    onChange={(e) => {
                      setDesc(e.target.value);
                      if (desc.length !== 0) {
                        setIsEmpty(false);
                      }
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <Autocomplete
                    value={type}
                    onChange={(event, newValue) => {
                      setType(newValue);
                    }}
                    options={projectsType}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        label="Project type"
                        variant="filled"
                      />
                    )}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <Autocomplete
                    value={location}
                    onChange={(event, newValue) => {
                      setLocation(newValue);
                    }}
                    options={cities}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        label="Where are from"
                        variant="filled"
                      />
                    )}
                  />
                </Grid>
                <Grid item xs={12} sm={12}>
                  <Autocomplete
                    disablePortal
                    value={teamNeeded}
                    onChange={(event, newValue) => {
                      setTeamNeeded(newValue);
                    }}
                    options={projectsTeam}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        label="Number of teammates"
                        variant="filled"
                      />
                    )}
                  />
                </Grid>

                <Grid item xs={12} sm={12}>
                  <Autocomplete
                    value={level}
                    onChange={(event, newValue) => {
                      setLevel(newValue);
                    }}
                    options={projectsLevel}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        label="Level of your project"
                        variant="filled"
                      />
                    )}
                  />
                </Grid>
              </Grid>

              <div className="form-group d-flex align-items-center justify-content-between my-2">
                <Button
                  type="submit"
                  variant="contained"
                  sx={{
                    ":hover": {
                      bgcolor: "#e4a300",
                    },
                    maxWidth: "180px",
                    maxHeight: "30px",
                    minWidth: "180px",
                    minHeight: "30px",

                    mt: 3,
                    mb: 2,
                    backgroundColor: "#FFB703",
                  }}
                >
                  Add
                </Button>

                <Button
                  type="submit"
                  variant="contained"
                  sx={{
                    ":hover": {
                      bgcolor: "#e4a300",
                    },
                    maxWidth: "180px",
                    maxHeight: "30px",
                    minWidth: "180px",
                    minHeight: "30px",
                    mt: 3,
                    mb: 2,
                    backgroundColor: "#FFB703",
                  }}
                  onClick={() => navigate("/myprojects")}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </Box>
        </Box>
      </Container>
    </>
  );
};

const mapStateToProps = (state) => ({
  projects: state,
});
const mapDispatchToProps = (dispatch) => ({
  addProject: (data) => {
    dispatch({ type: "ADD_PROJECT", payload: data });
  },
});

const projectsType = [
  "Web Application",
  "Android Application",
  "IOS Application",
  "Security System",
  "UI",
  "UX",
];

const projectsTeam = [1, 2, 3, 4, 5, 6];

const projectsLevel = ["Guru", "Advanced", "Intermediate", "Junior", "Newbie"];

const cities = [
  "Riyadh",
  "Jeddah",
  "Mecca",
  "Medina",
  "Ad Dammam",
  "Al Hufuf",
  "Buraydah",
  "Al Hillah",
  "At Taif",
  "Tabuk",
  "Khamis Mushayt",
  "Hail",
  "Al Qatif",
  "Al Mubarraz",
  "Al Kharj",
  "Najran",
  "Yanbu‘",
  "Abha",
  "Arar",
  "Jazan",
  "Sakaka",
  "Al Bahah",
];

export default connect(mapStateToProps, mapDispatchToProps)(AddProject);
