import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import Nav from "../../../../Nav";
import Button from "@mui/material/Button";
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import EditIcon from '@mui/icons-material/Edit';

const UserProjects = ({ projects, deleteProject }) => {
  return (
    <>
      <Nav />
      <div style={{ paddingTop: "200px" }}>
        <div
          className="container col-md-12 mx-auto shadow"
          style={{
            paddingTop: "70px",
            paddingBottom: "70px",
            marginBottom: "200px",
          }}
        >
          <h1
            className="text-center py-3 display-2"
            style={{ fontSize: "40px", fontWeight: "600", color: "#00468B" }}
          >
            My Projects
          </h1>

          <div className="row d-flex flex-column">
            <div className="col-md-10 mx-auto my-4">
              <table className="table table-hover">
                <thead
                  className="table-header  text-white "
                  style={{ backgroundColor: "#00468B" }}
                >
                  <tr>
                    <th scope="col">Id</th>
                    <th scope="col">Name</th>
                    <th scope="col">Type</th>
                    <th scope="col">Description</th>
                    <th scope="col">Team Needed</th>
                    <th scope="col">Location</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>
                  {projects.length > 0 ? (
                    projects.map((project, id) => (
                      <tr key={id}>
                        <td>{id + 1}</td>
                        <td>{project.name}</td>
                        <td>{project.type}</td>
                        <td>{project.desc}</td>
                        <td>{project.location}</td>
                        <td>{project.teamNeeded}</td>
                        <td>
                          {/* <Link
                            to={`/myprojects/edit/${project.id}`}
                            className="btn btn-sm mr-1"
                          >
                            Edit
                          </Link> */}
                          <Tooltip title="Edit">
                            <IconButton>

                              <EditIcon />
                            </IconButton>
                          </Tooltip>

                          <Tooltip title="Delete">
                            <IconButton
                            onClick={() => deleteProject(project.id)}

                            >
                              <DeleteIcon />
                            </IconButton>
                          </Tooltip>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <th>No projects found</th>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                ":hover": {
                  bgcolor: "#e4a300",
                },
                maxWidth: "180px",
                maxHeight: "30px",
                minWidth: "180px",
                minHeight: "30px",
                mt: 3,
                mb: 2,
                backgroundColor: "#FFB703",
                marginLeft: "550px",
              }}
            >
              <Link
                to="/myprojects/add"
                style={{ textDecoration: "none", coloe: "white" }}
              >
                Add New Project
              </Link>
            </Button>
          </div>
        </div>
      </div>
    </>
  );
};

const mapStateToProps = (state) => ({
  projects: state,
});

const mapDispatchToProps = (dispatch) => ({
  deleteProject: (id) => {
    dispatch({ type: "DELETE_PROJECT", payload: id });
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(UserProjects);
