import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

const UserProjects = ({ projects, deleteProject }) => {
  return (
    <div className="container">
      <div className="row d-flex flex-column">
        <Link to="/myprojects/add" className="btn btn-outline-dark my-5 ml-auto ">
          Add Project
        </Link>
        <div className="col-md-10 mx-auto my-4">
          <table className="table table-hover">
            <thead className="table-header bg-dark text-white">
              <tr>
                <th scope="col">Id</th>
                <th scope="col">Name</th>
                <th scope="col">Type</th>
                <th scope="col">Description</th>
                <th scope="col">Team Needed</th>
                <th scope="col">Location</th>
                <th scope="col"></th>
              </tr>
            </thead>
            <tbody>
              {projects.length > 0 ? (
                projects.map((project, id) => (
                  <tr key={id}>
                    <td>{id + 1}</td>
                    <td>{project.name}</td>
                    <td>{project.type}</td>
                    <td>{project.desc}</td>
                    <td>{project.location}</td>
                    <td>{project.teamNeeded}</td>
                    <td>
                      <Link
                        to={`/myprojects/edit/${project.id}`}
                        className="btn btn-sm btn-primary mr-1"
                      >
                        Edit
                      </Link>
                      <button
                        type="button"
                        onClick={() => deleteProject(project.id)}
                        className="btn btn-sm btn-danger"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <th>No projects found</th>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  projects: state,
});

const mapDispatchToProps = (dispatch) => ({
  deleteProject: (id) => {
    dispatch({ type: "DELETE_PROJECT", payload: id });
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(UserProjects);
