import * as React from "react";
import { styled, alpha } from "@mui/material/styles";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import InputBase from "@mui/material/InputBase";
import Badge from "@mui/material/Badge";
import MenuItem from "@mui/material/MenuItem";
import Menu from "@mui/material/Menu";
import MenuIcon from "@mui/icons-material/Menu";
import SearchIcon from "@mui/icons-material/Search";
import AccountCircle from "@mui/icons-material/AccountCircle";
import MoreIcon from "@mui/icons-material/MoreVert";
import logo from "../../img/logo.png";
import { useNavigate } from "react-router";
import SendIcon from "@mui/icons-material/Send";
import { useEffect, useState } from "react";
import HomeIcon from "@mui/icons-material/Home";
import Tooltip from "@mui/material/Tooltip";
import { useTheme, ThemeProvider, createTheme } from "@mui/material/styles";
import Brightness4Icon from "@mui/icons-material/Brightness4";
import Brightness7Icon from "@mui/icons-material/Brightness7";

const ColorModeContext = React.createContext({ toggleColorMode: () => {} });

export default function Nav(props) {
  const [mode, setMode] = React.useState("light");
  const colorMode = React.useMemo(
    () => ({
      toggleColorMode: () => {
        setMode((prevMode) => (prevMode === "light" ? "dark" : "light"));
      },
    }),
    []
  );

  const theme = React.useMemo(
    () =>
      createTheme({
        palette: {
          mode,
        },
      }),
    [mode]
  );
  useEffect(() => {
    console.log(props);
  }, [props]);

  const [anchorEl, setAnchorEl] = useState(null);
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);

  const navigate = useNavigate();

  const isMenuOpen = Boolean(anchorEl);
  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
    props.setSearch(false);
  };

  const handleMobileMenuClose = () => {
    setMobileMoreAnchorEl(null);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    handleMobileMenuClose();
  };

  const handleMenuCloseSignup = () => {
    setAnchorEl(null);
    handleMobileMenuClose();
    navigate("/signup");
  };

  const handleMenuCloseSignin = () => {
    setAnchorEl(null);
    handleMobileMenuClose();
    navigate("/signin");
  };

  const handleMenuCloseProjects = () => {
    setAnchorEl(null);
    handleMobileMenuClose();
    navigate("/myprojects");
  };

  const handleMenuCloseSignout = () => {
    setAnchorEl(null);
    handleMobileMenuClose();
    navigate("/");
  };

  const handleMobileMenuOpen = (event) => {
    setMobileMoreAnchorEl(event.currentTarget);
  };

  const menuId = "primary-search-account-menu";

  const renderMenu = (
    <Menu
      anchorEl={anchorEl}
      anchorOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
      id={menuId}
      keepMounted
      transformOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
      open={isMenuOpen}
      onClose={handleMenuClose}
    >
      <MenuItem onClick={handleMenuCloseSignin}>Sign In</MenuItem>
      <MenuItem onClick={handleMenuCloseSignup}>Sign Up</MenuItem>
    </Menu>
  );

  const renderMenuLogin = (
    <Menu
      anchorEl={anchorEl}
      anchorOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
      id={menuId}
      keepMounted
      transformOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
      open={isMenuOpen}
      onClose={handleMenuClose}
    >
      <MenuItem onClick={handleMenuCloseProjects}>My Projects</MenuItem>
      <MenuItem
        onClick={() => {
          props.setLogin(false);
          handleMenuCloseSignout();
        }}
      >
        Sign Out
      </MenuItem>
    </Menu>
  );

  const mobileMenuId = "primary-search-account-menu-mobile";
  const renderMobileMenu = (
    <Menu
      anchorEl={mobileMoreAnchorEl}
      anchorOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
      id={mobileMenuId}
      keepMounted
      transformOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
      open={isMobileMenuOpen}
      onClose={handleMobileMenuClose}
    >
      <MenuItem>
        <IconButton size="large" aria-label="show 4 new mails" color="inherit">
          <Badge>
            <HomeIcon />
          </Badge>
        </IconButton>
        <p style={{ paddingTop: "16px" }}>Requests</p>
      </MenuItem>
      <MenuItem onClick={handleProfileMenuOpen}>
        <IconButton
          size="large"
          aria-label="account of current user"
          aria-controls="primary-search-account-menu"
          aria-haspopup="true"
          color="inherit"
        >
          <AccountCircle />
        </IconButton>
        <p style={{ paddingTop: "16px" }}>Profile</p>
      </MenuItem>
    </Menu>
  );

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" sx={{ backgroundColor: "#00468B" }}>
        <Toolbar>
          <img
            src={logo}
            alt=""
            width="2.5%"
            style={{ margin: "10px 0px 10px 20px" }}
          />

          {(props.search && (
            <Search>
              <SearchIconWrapper>
                <SearchIcon />
              </SearchIconWrapper>
              <StyledInputBase
                placeholder="Search…"
                inputProps={{ "aria-label": "search" }}
                onChange={(e) => props.setSearchTerm(e.target.value)}
              />
            </Search>
          )) ||
            null}

          <Box sx={{ flexGrow: 1 }} />
          <Box sx={{ display: { xs: "none", md: "flex" } }}>
            {(props.login && (
              <Tooltip title="Home">
                <IconButton
                  size="large"
                  color="inherit"
                  onClick={() => {
                    props.setSearch(false);
                    navigate("/projects");
                  }}
                >
                  <Badge color="error">
                    <HomeIcon />
                  </Badge>
                </IconButton>
              </Tooltip>
            )) || (
              <Tooltip title="Home">
                <IconButton
                  size="large"
                  aria-label="show 4 new mails"
                  color="inherit"
                  onClick={() => {
                    navigate("/");
                  }}
                >
                  <Badge color="error">
                    <HomeIcon />
                  </Badge>
                </IconButton>
              </Tooltip>
            )}

            <ColorModeContext.Provider value={colorMode}>
              <ThemeProvider theme={theme}>
                <MyApp />
              </ThemeProvider>
            </ColorModeContext.Provider>

            {(props.login && (
              <Tooltip title="Requests">
                <IconButton
                  size="large"
                  aria-label="show 4 new mails"
                  color="inherit"
                >
                  <Badge color="error" badgeContent={props.request}>
                    <SendIcon />
                  </Badge>
                </IconButton>
              </Tooltip>
            )) ||
              null}

            <Tooltip title="User">
              <IconButton
                size="large"
                edge="end"
                aria-label="account of current user"
                aria-controls={menuId}
                aria-haspopup="true"
                onClick={handleProfileMenuOpen}
                color="inherit"
              >
                <AccountCircle />
              </IconButton>
            </Tooltip>
          </Box>
          <Box sx={{ display: { xs: "flex", md: "none" } }}>
            <IconButton
              size="large"
              aria-label="show more"
              aria-controls={mobileMenuId}
              aria-haspopup="true"
              onClick={handleMobileMenuOpen}
              color="inherit"
            >
              <MoreIcon />
            </IconButton>
          </Box>
        </Toolbar>
      </AppBar>
      {renderMobileMenu}
      {props.login ? renderMenuLogin : renderMenu}
    </Box>
  );
}

const Search = styled("div")(({ theme }) => ({
  position: "relative",
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  "&:hover": {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginRight: theme.spacing(2),
  marginLeft: 0,
  width: "100%",
  [theme.breakpoints.up("sm")]: {
    marginLeft: theme.spacing(3),
    width: "auto",
  },
}));

const SearchIconWrapper = styled("div")(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: "100%",
  position: "absolute",
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: "inherit",
  "& .MuiInputBase-input": {
    padding: theme.spacing(1, 1, 1, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create("width"),
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      width: "37ch",
      "&:focus": {
        width: "50ch",
      },
    },
  },
}));

function MyApp(props) {
  const theme = useTheme();
  const colorMode = React.useContext(ColorModeContext);
  return (
    <div
      sx={{
        display: "flex",
        width: "100%",
        alignItems: "center",
        justifyContent: "center",
        bgcolor: "background.default",
        color: "text.primary",
        borderRadius: 1,
        p: 3,
      }}
    >
      {theme.palette.mode === "dark" ? (
        <IconButton
          sx={{ ml: 1, paddingTop: "12px" }}
          onClick={() => {
            props.setDark(true);
            colorMode.toggleColorMode();
          }}
          color="inherit"
        >
          <Tooltip title="Light mode">
            <Brightness4Icon />
          </Tooltip>
        </IconButton>
      ) : (
        <IconButton
          sx={{ ml: 1, paddingTop: "12px" }}
          onClick={() => {
            props.setDark(false);
            colorMode.toggleColorMode();
          }}
          color="inherit"
        >
          <Tooltip title="Dark mode">
            <Brightness7Icon />
          </Tooltip>
        </IconButton>
      )}
    </div>
  );
}
